import React, { useState, useEffect } from 'react';
import { Redirect } from 'react-router-dom';

const RecipePage = ({ isLoggedIn, socket }) => {
  const [recipe, setRecipe] = useState('');
  const [ingredients, setIngredients] = useState('');
  const [error, setError] = useState(null);

  useEffect(() => {
    if (isLoggedIn) {
      socket.on('recipe', (data) => {
        setRecipe(data);
      });
    }
  }, [isLoggedIn, socket]);

  if (!isLoggedIn) {
    return <Redirect to="/login" />;
  }

  const handleFormSubmit = (event) => {
    event.preventDefault();
    if (ingredients.trim() === '') {
      setError('Please enter some ingredients.');
      return;
    }
    socket.emit('request-recipe', { ingredients });
    setIngredients('');
    setError(null);
  };

  return (
    <div className="container mt-5">
      <h2>Recipe Suggestions</h2>
      <form onSubmit={handleFormSubmit}>
        <div className="form-group">
          <label htmlFor="ingredientsInput">Ingredients</label>
          <input type="text" className="form-control" id="ingredientsInput" value={ingredients} onChange={(e) => setIngredients(e.target.value)} placeholder="Enter ingredients separated by commas" />
          {error && <div className="alert alert-danger" role="alert">{error}</div>}
        </div>
        <button type="submit" className="btn btn-primary">Get Recipe</button>
      </form>
      {recipe && (
        <div className="mt-4">
          <h3>Recipe</h3>
          <p>{recipe}</p>
        </div>
      )}
    </div>
  );
};

export default RecipePage;
